$(document).ready(function(){$.getJSON("../data/twitter.json",function(e){$.each(e,function(e,t){$("#twitter-widget").append('<div class="widgets__tweet"><p class="widgets__text"><em>'+t.nameLink1+"</em>"+t.text+"<em>"+t.nameLink2+'</em><span class="widgets__tweet-date">'+t.date+"</span></p></div>")})}),setInterval(function(){var e=$(".widgets__tweet").length,t=Math.ceil(e/2),a=Math.floor(Math.random()*t),d=Math.floor(t+Math.random()*(e-t));$(".widgets__tweet").eq(a).fadeIn(500).delay(4e3).fadeOut(500),$(".widgets__tweet").eq(d).fadeIn(500).delay(4e3).fadeOut(500)},5e3)});